import React, { useState } from "react";
import "./Login.css";


const handleRegister = () => {};

function LoginForm ({ Login, error }) {
    const [details, setDetails] = useState({ name: "", email: "", password: "" });
    
    const handleLogin = e => {
        e.preventDefault();
        Login(details);
    }

    return (
    <div className="cover">
      <h1>Login</h1>
      {(error != "") ? (<div className="error">{error}</div>) : ""}
      <input type="email" placeholder="Email" onChange={ e =>  setDetails({...details, email: e.target.value})} value = {details.email}/>
      <input type="text" placeholder="Username" onChange={ e =>  setDetails({...details, name: e.target.value})} value = {details.name}/>
      <input type="password" placeholder="Password" onChange={ e =>  setDetails({...details, password: e.target.value})} value = {details.password}/>
      <div className="alt_login">
        <div className="login_btn" onClick={handleLogin}>
          Login
        </div>
        <div className="register_btn" onClick={handleRegister}>
          Register
        </div>
      </div>
    </div>
  );
};
export default LoginForm;
